downloadFile(url, callback) {
    var haveIframe = $("iframe");
    if (haveIframe) {
        haveIframe.remove();
    }
    try {
        var elemIF = document.createElement("iframe");
        elemIF.src = url;
        elemIF.style.display = "none";
        document.body.appendChild(elemIF);
    } catch (e) {
        callback && callback(e);
    }
}
